style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("9gag.com") {

/* Disable ads */
.cp-background,.video-ads,.naughty-box,.block-ad,#jsid-sticky-button,#jsid-idle-popup-container,.topBannerAd-container{display:none!important;}
div[style="width: 500px; height: 281px; z-index: 1; position: relative; overflow: hidden;"]{display:none!important;}


/* remove space */
post-comment{margin-top:0!important;}

/* remove colors
.overlay-scroll-container{background-color:rgba(0,0,0,0);overflow:hidden;}
*/
/* fix for right sidebar broken in FireFox(atleast) */
#sidebar-content {position: static !important}

/* disable section on left side */
section[class=""] {display:none!important;}
.footer {display:none!important;}

/* disable multi color bagground */
.gif-post,.post-container,.video-post {background-color: transparent !important;}

/* annoying videos 
iframe{display:none!important;}*/
article > div.post-container:first-child{display:none!important;}

/* test */
html background-white div #container .page .main-wrap section div div article {padding:0;border-top:0;margin-bottom: 15px}
section > div > div > article > header:nth-child(1) > div:nth-child(1) {display:none!important;}
.post-tag,.post-meta,.post-afterbar-a.in-list-view{display:none}
.featured-tag {display:none!important;}

}
