/* i really want this to be global */


#map-container .map-location-tooltip .energy-required, #map-container .map-location-tooltip .time-to-explore{
	left: -75px; top: 20px;
}

.pet-interface-container #right-container .lens {
    border-radius: 20px;
    background:rgba(255, 255, 255, .89);
}
#map-container .map-location-tooltip .map-location-name{
	z-index: 25;
	left: 10px;
	top: 25px;
	width: 200px;
}

#map-container .map-location-tooltip .map-location-infos{
	box-shadow: none;
	z-index: 20;
	background:rgba(255, 255, 255, 0);
}


