style.css:
@-moz-document domain("colorzilla.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. ______________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}

@-moz-document domain("colorzilla.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

#main-nav li a {
	text-transform: capitalize!important;
}

.menu.desktop a,
.pane,
.stop-details .opacity-adjust-open-button,
:focus,
body #hd
{
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.gradient-name .name-input,
.preview .output-options .dimension,
.stop-details .opacity-value,
.stop-details .position {
    text-align: center;
}

/*___ Fonts ________________________________________________________________________*/

.css-output div.css-output-text,
.gradient-name .name-input,
body {
	font-size: 19px!important;
}

.browser-compatibility td .more-info,
button,
input,
select
{
	font-size: 15px!important;
}

/*___ Remove Items _________________________________________________________________*/

.gradient-ad-panel,
.share-links
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.gradient-name {
    margin-top: 8rem;
}

.stop-details .color {
    margin-left: 6px;
}

.stop-details .opacity-value {
    margin-left: 23px;
}

#main-nav {
    right: 29px;
}

/*___ Margins ______________________________________________________________________*/

.css-output div.css-output-text {
	margin: 12px!important;
}

#editor-heading,
.gradient-control,
.info-panel
{
	margin: 12px auto!important;
}

.css-output div.css-output-text div {
    margin: 12px 0!important;
}

/*___ Paddings _____________________________________________________________________*/

#doc2,
.column.left,
.column.right,
.presets .presets-container
{
	padding: 12px!important;
}

.browser-compatibility td .more-info .syntax,
.browser-compatibility td .more-info,
.stop-details .delete-button button,
button,
input,
select
{
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

.css-output div.css-output-text {
	max-height: none!important;
}

.browser-compatibility td .more-info,
.presets .presets-container {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

body {
	max-width: none!important;
}

selector {
	width: auto!important;
}

.dialog-panel,
.permalink-panel
{
    width: 100%;
}

#editor-heading,
.gradient-control,
.info-panel
{
	width: 98%!important;
}

#doc2 {
	width: 96%!important;
}

.column.left,
.column.right
{
	width: 46%!important;
}

.preview .output-options .dimension {
    width: 3em;
}





}

@-moz-document domain("colorzilla.com") {
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

.gradient-adjustments-panel .adjustment-ops-panel a:hover,
.gradient-name .save-new-preset:hover,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

.browser-compatibility td.prefixed:not(.not-supported) .syntax,
tr td,
.browser-compatibility td.not-supported div,
.colorpicker .dialog-button,
.css-output div.css-output-text,
.gradient-name .save-new-preset,
.permalink-panel span,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Khaki _______________________________________________________________________________________________*/

h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Burlywood ___________________________________________________________________________________________*/

.dialog-panel .panel-label,
h3,
h4,
li strong,
p strong {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown _________________________________________________________________________________________*/

.css-output .css-options,
.label,
.preview .output-options,
.stop-details .disabled,
.stop-details .position-details,
.stop-details .stop-type-label,
h5,
h6,
html,
label
{
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue ________________________________________________________________________________________________*/

time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left:6px;
}

/*________________________________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}


}

@-moz-document domain("colorzilla.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

/*___ Background Gray ______________________________________________________________*/

.dialog-panel .panel-label {
	background-color: #060505!important;
}

/*___ Background 0000 ______________________________________________________________*/

.pane,
body #hd {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.menu.desktop a.enabled:hover,
.stop-details .opacity-adjust-open-button.disabled
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Green ________________________________________________________________________*/

.gradient-name button {
	background-image: linear-gradient(to bottom right, #5dbc2e, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.gradient-name button:hover {
	background-image: linear-gradient(to bottom right, #6ecd3f, #000)!important;
	color: #ff0!important;
}

.gradient-name button:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Red ________________________________________________________________________*/

.stop-details .delete-button button {
	background-image: linear-gradient(to bottom right, #bc2e2e, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.stop-details .delete-button button:hover {
	background-image: linear-gradient(to bottom right, #cd3f3f, #000)!important;
	color: #ff0!important;
}

.stop-details .delete-button button:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

#doc2,
#editor-heading,
.css-output div.css-output-text,
.gradient-control,
.info-panel,
.inner-sub-panel,
.menu.desktop,
.presets .presets-container
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#doc2:hover,
#editor-heading:hover,
.css-output div.css-output-text:hover,
.gradient-control:hover,
.info-panel:hover,
.inner-sub-panel:hover,
.menu.desktop:hover,
.presets .presets-container:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.browser-compatibility td div,
.column.right,
.column.left,
body .product {
	background-color: #1a1a1a!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.browser-compatibility td div:hover,
.column.right:hover,
.column.left:hover,
body .product:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.browser-compatibility td.empty,
.browser-compatibility td.not-supported {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Blue Gradient _________________________________________________________________*/

.browser-compatibility td.partial
{
	background: linear-gradient(to bottom, #f9f9cf, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient ________________________________________________________________*/

.browser-compatibility td.prefixed {
	background: linear-gradient(to bottom, #cff9d6 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Yellow Gradient _______________________________________________________________*/

.browser-compatibility td {
	background: linear-gradient(to bottom, #a6edb2 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Orange Gradient _______________________________________________________________*/

.browser-compatibility td .more-info .syntax {
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}








}

@-moz-document domain("colorzilla.com") {
/*___________________________________________________________________________________________*/

.css-output div.css-output-text div,
.presets .preset-item-background,
body .product
{
	filter: brightness(65%)!important;
}

/*___________________________________________________________________________________________*/

.css-output div.css-output-text div:hover,
.presets .preset-item-background:hover,
body .product:hover
{
	filter: brightness(100%)!important;
}

/*___________________________________________________________________________________________*/

.gradient-background,
.presets .preset-item,
.presets .preset-item-background
{
	border-radius: 22px!important;
}

/*___________________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

.info-panel li,
p {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

.info-panel li:hover,
p:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}
}

@-moz-document domain("colorzilla.com") {

/* Arrows _______________________________________________________________*/

.stop-details .opacity-adjust-open-button {
	background: url(https://i.ibb.co/LQDKzQT/right-arrow-30x30.png) no-repeat!important;
	box-sizing: border-box;
}

.stop-details .opacity-adjust-open-button {
    background-size: 21px!important;
    height: 26px!important;
    padding-left: 22px!important;
    width: 10px!important;
}


}
