/* ==UserStyle==
@name			RAW LH
@author			tyf
@preprocessor	default
==/UserStyle== */

@-moz-document domain("loveheaven.net"), domain("loveha.net"), domain("18lhplus.com"), domain("batoscan.net")
{
	body>br,
	body>iframe,
	#myCarousel,
	#report_error,
	#sbottom,
	#fb-root,
	#modal-1,
	#content>center,
	#contentstory>br,
	#contentstory>center,
	#content>br,
	.col-lg-4> :not(div),
	.chapter-content br,
	.chapter-content center,
	.clear-fix,
	.like-box,
	.float-ck,
	.seoonpage,
	.row-list hr,
	.ads,
	.adtrue-div,
	.quang-cao,
	.end,
	.modal-backdrop,
	.mrb10,
	.sticky_bottom,
	.doreamon>center,
	.panel>center,
	center>br,
	a.chapter[href="#"],
	div[id^="rn_ad_"],
	img[src="https://s4.imgmirror.club/images/20200112/5e1ad960d67b2_5e1ad962338c7.jpg"],
	img[src="https://imgmirror.club/images/20200309/fe132b3d32acc39f5adcea9075bedad4LoveHeaven.jpg"],
	img[src="https://s4.imgmirror.club/images/20200715/image_5f0ecf23aed2e.png"] {
		display: none !important;
	}
	
	html,
	body {
		height: auto;
	}
	
	body {
		font: 400 12pt sans-serif;
		margin-top: 5mm;
		background: linear-gradient(90deg,
				#333 0,
				#777 45%,
				#777 55%,
				#333 100%)
	}
	
	h1,
	.h1 {
		font-size: 14pt;
		margin: .3em 0
	}
	
	h2,
	h3 {
		margin: .5em 0 .3em
	}
	
	img {
		object-fit: contain;
	}
	
	time {
		display: inline-block;
		min-width: 7em;
	}
	
	#header .navbar-collapse:not(:last-of-type) {
		float: right;
		width: auto;
	}
	
	.search {
		max-height: 36px;
		margin: .4em 2em 1em 0;
		display: block;
		overflow: hidden;
		z-index: 99;
		opacity: .7;
		transition: max-height .6s, opacity .5s .6s;
		box-shadow: 0 1mm 3mm #333;
	}
	
	.search:hover {
		max-height: 50vh;
		overflow-y: scroll;
		transition: max-height .3s, opacity .3s;
	}
	
	ul.ss-box li.ss-result a {
		padding: 0;
	}
	
	.ss-result table {
		min-height: 70px;
	}
	
	#pop_manga {
		display: none !important;
		border-radius: 2mm;
		overflow: hidden;
		box-shadow: 0 0 3mm #333;
	}
	
	.headroom {
		top: -40px !important;
		background-color: rgba(180, 100, 60, .7);
		opacity: .8;
		border-bottom: 1mm solid #d72;
		box-shadow: 0 0 3mm #555;
		transform: none !important;
		transition: all .8s 0.6s;
		backdrop-filter: blur(5mm);
		-webkit-backdrop-filter: blur(5mm);
	}
	
	.headroom:hover {
		top: 0 !important;
		background-color: #df691a;
		z-index: 100 !important;
		opacity: .95;
		transition: all .1s;
	}
	
	.navbar {
		margin: 0;
	}
	
	.chapter-before .chapter-select {
		position: fixed;
		left: 0;
		bottom: -35px;
		width: 100vw;
		padding: 5px 0;
		background-color: rgba(60, 60, 60, .7);
		opacity: .5;
		z-index: 9999;
		box-shadow: 0 -2px 1mm rgba(0, 0, 0, .4);
		transition: .8s .6s;
		backdrop-filter: blur(1mm);
		-webkit-backdrop-filter: blur(1mm);
	}
	
	.chapter-before .chapter-select:hover {
		bottom: 0;
		background-color: rgba(128, 128, 128, .5);
		opacity: .95;
		backdrop-filter: blur(5mm);
		-webkit-backdrop-filter: blur(5mm);
		transition: .2s;
	}
	
	body>.container,
	.mCustomScrollbar>.container {
		position: relative;
		min-width: 670px;
		padding: 0 3mm;
		margin: 0;
		overflow-x: hidden;
	}
	
	::-webkit-scrollbar {
		width: 2pt;
		height: 2pt;
		background: rgba(255, 255, 255, .5);
	}
	
	#contentstory,
	.backgroundhide {
		margin: 0 0 3mm;
	}
	
	.doreamon {
		font-size: 11pt;
		padding: 0 .5em;
	}
	
	.doreamon:after {
		display: block;
		content: "";
		height: 1.5mm;
		clear: both;
	}
	
	.chapter-content-top {
		margin: 0;
	}
	
	.carousel-inner>img {
		height: 100px;
		overflow: hidden
	}
	
	.media-object {
		object-fit: cover;
	}
	
	#content,
	.chapter-content {
		font-size: 0;
		width: calc(100% - 4mm) !important;
		max-width: calc((100vh - 22mm)*1.5);
		text-align: center;
		margin: 0 auto;
	}
	
	.chapter-select {
		direction: ltr;
	}
	
	.swiper-container {
		height: 94vh;
		padding-top: 0;
	}
	
	.swiper-container,
	.swiper-wrapper {
		min-height: calc(100vh - 52px) !important;
	}
	
	.swiper-wrapper,
	.swiper-container .swiper-slide,
	.swiper-container .chapter-img {
		max-height: 100% !important;
	}
	
	.list-chapters .list-wrap p {
		margin: 0;
	}
	
	.list-chapters .title {
		display: inline-block;
		line-height: 2;
		width: calc(100% - 260px);
	}
	
	.swiper-wrapper .white-slide {
		background-color: rgba(255, 255, 255, .4);
	}
	
	.chapter-pagination {
		top: 0;
	}
	
	#content>img,
	.chapter-content .chapter-img {
		min-width: 45vh;
		max-width: 100%;
		height: calc(100vh - 22mm);
		background-color: rgba(255, 255, 255, .4);
		border: none;
		margin: 0 auto 16px;
	}
	
	.chapter-content .well {
		width: 80vw;
		min-width: 500px !important;
		max-width: 900px;
		margin: 0 auto;
		padding: 0 1em;
		border-radius: 3mm;
	}
	
	.page-header {
		margin: 0;
		padding: 0;
	}
	
	.itemupdate,
	.row-list {
		vertical-align: top;
		font-size: 11pt;
		position: relative;
		width: calc(100% / 2 - 6px);
		height: calc(18vh + 40px);
		display: inline-block;
		margin: 6px 1px 0;
		background: #ccccd3;
		border-radius: 1.5mm;
		box-shadow: 0 0 0 1px rgba(0, 0, 0, .2);
		overflow: hidden;
		transition: background-color .2s, box-shadow .2s, height .2s
	}
	
	.itemupdate:hover,
	.row-list:hover {
		margin: 5px 1px 1px;
		background-color: #dde;
		box-shadow: 0 0 0 1px #86c, 0 2px 10mm rgba(0, 0, 0, .3);
		z-index: 2;
		transition: background-color 0s, box-shadow .1s;
	}
	
	.itemupdate .group .title-h3 {
		padding-left: 53%;
		text-indent: -112%;
		display: -webkit-box;
		-webkit-line-clamp: 2;
		-webkit-box-orient: vertical;
		overflow: hidden;
		text-overflow: ellipsis;
	}
	
	.itemupdate .group>li:not(:first-child) {
		padding-left: 53%;
	}
	
	.itemupdate .chapter {
		font-size: 11pt;
		margin-bottom: 3mm;
	}
	
	.itemupdate .view,
	.itemupdate time {
		font-size: 11pt;
	}
	
	.row-list .pull-left,
	.itemupdate>a.cover {
		position: absolute;
		top: 2em;
		width: calc(50% - 1mm);
		padding: 0 2mm;
		margin: 0;
	}
	
	.doreamon .cover img,
	img.myimg,
	img.media-object.img-thumb {
		width: 100%;
		height: 18vh;
		padding: 2px;
		background-color: #fff;
		border-radius: 1mm;
		box-shadow: 0 0 1mm #333;
		object-fit: cover;
	}
	
	.media-body {
		margin: 2.7em 1mm 0 49%;
	}
	
	.media-body .media-heading {
		position: absolute;
		top: 2mm;
		left: 0;
		width: calc(100% - 2mm);
		text-indent: -100%;
		padding-left: 50%;
		margin: 0;
		display: -webkit-box;
		-webkit-line-clamp: 2;
		-webkit-box-orient: vertical;
		overflow: hidden;
	}
	
	.media-heading a {
		color: maroon;
		font-size: 11pt;
		text-shadow: 1px 1px 2px #fff;
	}
	
	.media-heading a:hover {
		text-decoration: none;
	}
	
	.media-body small {
		display: inline-block;
		width: 100%;
		max-height: 15mm;
		font-size: 0;
		overflow: hidden;
		margin: 2mm 0 2mm -1mm;
		padding: 1mm;
		border-radius: 5px;
		background: rgba(120, 120, 120, .1);
		box-shadow: inset 0 0 1mm rgba(0, 0, 0, .2);
		transition: linear .3s;
	}
	
	.media-body small:hover {
		max-height: 40mm;
		overflow: auto;
		background: rgba(255, 255, 255, .3);
		box-shadow: 0 0 3px 1px rgba(0, 0, 0, .3);
	}
	
	.btn {
		font-size: 10pt;
		margin: 1px 2px 1px 0;
	}
	
	.btn-xs {
		color: #ddd;
		line-height: 1;
		padding: .2em;
		border: none;
		border-radius: 1mm;
	}
	
	.btn-xs:hover {
		color: #fff;
	}
	
	.itemupdate .title-h3 {
		margin: .5em 0;
		color: maroon;
		font: bold 11pt Helvetica;
		text-shadow: 0 0 2mm white;
	}
	
	.hinhnen1 {
		padding: 3mm;
	}
	
	.col-lg-8,
	.info-manga {
		min-width: 600px;
		margin: 0 !important;
	}
	
	.col-lg-4>div {
		width: auto;
		display: block;
	}
	
	.col-lg-4 .panel-body {
		column-count: 2;
	}
	
	.col-lg-4 .panel-body .pull-left,
	.col-lg-4 .panel-body .pull-right {
		float: none !important;
		display: inline;
	}
	
	.breadcrumb {
		padding: 1mm 2mm;
		margin: 0 0 2mm;
	}
	
	.breadcrumb>li+li:before {
		color: #c61;
		content: ">"
	}
	
	.breadcrumb a,
	.itemupdate a.chapter {
		display: inline-block;
		padding: 0 1.5mm;
		border-radius: 1.5em;
		text-decoration: none;
	}
	
	.col-lg-4 .panel-body a {
		display: inline-block;
		padding: 2px 2mm;
		margin: 2px 0;
		line-height: 1;
		border-radius: 1em;
		text-decoration: none;
	}
	
	.breadcrumb a:hover,
	.itemupdate a.chapter:hover,
	.col-lg-4 .panel-body a:hover {
		color: #fff;
		background-color: #a32;
	}
	
	.row-list .media,
	.media-body {
		overflow: visible;
	}
	
	.pop_info .group p {
		margin-left: 190px;
	}
	
	.pop_info .group strong {
		float: left;
		margin-left: -80px;
	}
	
	.xt {
		clear: both;
	}
	
	.btn-info {
		margin: 0 0 1.5mm;
	}
	
	.select-chapter {
		width: auto;
		min-width: 50mm;
		padding: 1mm 5mm 0;
	}
	
	#tab-chapper {
		position: relative;
		max-height: 12em !important;
		padding: 1px;
		box-shadow: 0 0 0 1px #777;
		border-radius: 2mm;
		overflow-x: hidden;
		overflow-y: scroll;
		transition: .5s .1s;
	}
	
	#tab-chapper:after {
		position: absolute;
		bottom: 0;
		left: 0;
		content: "";
		width: 100%;
		height: 60%;
		background: linear-gradient(0deg,
				rgba(128, 128, 128, 1) 1%,
				rgba(128, 128, 128, .7) 20%,
				rgba(128, 128, 128, 0) 100%);
		border-radius: 0 0 2mm 2mm;
		z-index: 3;
		transition: .3s .3s;
	}
	
	#tab-chapper:hover {
		max-height: 100vh !important;
		overflow: auto;
		transition: .5s;
	}
	
	#tab-chapper:hover:after {
		height: 0;
		transition: .3s;
	}
	
	#tab-chapper .tab-text {
		max-height: none !important;
	}
	
	#tab-chapper table,
	#tab-chapper ul {
		padding: 0;
		margin: 0;
	}
	
	#tab-chapper td {
		vertical-align: middle;
		padding: 0 2mm;
	}
	
	#tab-chapper a.chapter {
		display: block;
		padding: 1mm 0;
		text-decoration: none;
	}
	
	.table-hover>tbody>tr:hover>td {
		background: #fff
	}
	
	.pagination {
		margin: 3mm 0 0;
	}
	
	.title_doctruyen .centertext a.link_right {
		font-size: 18pt;
		padding: 0 25%;
		margin: 0 0;
		display: block;
	}
	
	.col-xs-1,
	.col-xs-2,
	.col-xs-3,
	.col-xs-4,
	.col-xs-5,
	.col-xs-6,
	.col-xs-7,
	.col-xs-8,
	.col-xs-9,
	.col-xs-10,
	.col-xs-11,
	.col-xs-12,
	.col-sm-1,
	.col-sm-2,
	.col-sm-3,
	.col-sm-4,
	.col-sm-5,
	.col-sm-6,
	.col-sm-7,
	.col-sm-8,
	.col-sm-9,
	.col-sm-10,
	.col-sm-11,
	.col-sm-12,
	.col-md-1,
	.col-md-2,
	.col-md-3,
	.col-md-4,
	.col-md-5,
	.col-md-6,
	.col-md-7,
	.col-md-8,
	.col-md-9,
	.col-md-10,
	.col-md-11,
	.col-md-12,
	.col-lg-1,
	.col-lg-2,
	.col-lg-3,
	.col-lg-4,
	.col-lg-5,
	.col-lg-6,
	.col-lg-7,
	.col-lg-8,
	.col-lg-9,
	.col-lg-10,
	.col-lg-11,
	.col-lg-12 {
		padding: 0;
	}
	
	.searchbutton {
		background: #fff url("https://rawqq.com/app/manga/themes/default/assets/images/btn_search.png") 50% 50% no-repeat
	}
	
	.fb_iframe_widget {
		white-space: nowrap;
		width: auto !important;
		min-width: 0 !important;
	}
	
	#myTabContent {
		padding: 0 5mm;
	}
	
	.tieude {
		margin: 0 auto !important;
	}
	
	img[src="/images/download1.gif"] {
		display: inline-block;
		width: auto;
		height: 7mm;
	}
	
	@media screen {
		select {
			font: 12pt sans-serif;
			line-height: 1.2;
		}
	
		.itemupdate {
			padding: 0;
		}
	
		.itemupdate ul.group {
			padding: 0 2mm;
			margin: 0;
		}
	
		.search-box .input-group {
			width: 300px;
		}
	
		.suggestsearch {
			border: none;
		}
	
		.media:first-child {
			padding: 0;
		}
	
		.sidebar-content>a {
			width: 35%;
		}
	
		.sidebar-content>a>img {
			width: 100%;
			height: 58px;
			padding: 0;
		}
	}
	
	@media screen and (min-width: 0) {
		.container {
			max-width: 100vw;
		}
	
		.suggestsearch {
			width: 370px;
		}
	
		.itemupdate,
		.row-list {
			height: calc(13.2vh + 2.5em);
			min-height: 200px;
		}
	
		img.myimg,
		.doreamon .cover img,
		img.media-object.img-thumb {
			height: 13.2vh;
			min-height: 160px;
		}
	}
	
	@media screen and (max-width: 1000px) {
	
		.col-lg-8,
		.info-manga {
			width: 100%;
		}
	
		.col-lg-4 {
			position: static;
			width: 100%;
			padding: 6px 6px 0;
			margin: 2mm 0 0;
			background-color: rgba(200, 200, 200, .8);
			overflow: hidden;
			border-radius: 2mm 0 0 2mm;
			box-shadow: 0 0 1mm #666;
		}
	
		.col-lg-4 .panel-body {
			column-count: 4;
		}
	}
	
	@media screen and (min-width: 1000px) {
	
		.col-lg-8,
		.info-manga {
			width: calc(100vw - 130px);
		}
	
		.col-lg-4 {
			position: absolute;
			width: 350px;
			right: -260px;
			padding: 1em;
			margin: 0;
			background-color: rgba(200, 200, 200, .8);
			overflow: hidden;
			border-radius: 2mm 0 0 2mm;
			box-shadow: 0 0 1mm #666;
			opacity: .5;
			z-index: 3;
			transition: all .8s;
		}
	
		.col-lg-4:hover {
			right: 0;
			opacity: 1;
			box-shadow: 0 0 2mm #444;
			transition: all .3s .2s;
			backdrop-filter: blur(5px);
			-webkit-backdrop-filter: blur(5px);
		}
	
		.col-lg-4 .panel-body {
			column-count: 2;
		}
	}
	
	@media screen and (max-width: 650pt) {
	
		.itemupdate,
		.row-list {
			width: calc(100% / 2 - 7px);
		}
	}
	
	@media screen and (min-width: 650pt) {
	
		.itemupdate,
		.row-list {
			width: calc(100% / 3 - 7px);
		}
	}
	
	@media screen and (min-width: 900pt) {
	
		.itemupdate,
		.row-list {
			width: calc(100% / 4 - 7px);
		}
	}
	
	@media screen and (min-width: 1200pt) {
	
		.itemupdate,
		.row-list {
			width: calc(100% / 5 - 7px);
		}
	
		.itemupdate,
		.row-list {
			height: calc(17.5vh + 2.5em);
		}
	
		img.myimg,
		.doreamon .cover img,
		img.media-object.img-thumb {
			height: 17.5vh;
		}
	}
	
	@media screen and (min-width: 1700pt) {
	
		.itemupdate,
		.row-list {
			width: calc(100% / 7 - 7px);
		}
	
		.itemupdate,
		.row-list {
			height: calc(22vh + 2.5em);
		}
	
		.media-body {
			margin-left: 55%;
		}
	
		.media-body .media-heading {
			padding-left: 55%;
			text-indent: -122%;
		}
	
		.row-list .pull-left,
		.itemupdate>a.cover {
			width: 55%;
		}
	
		.itemupdate .group .title-h3 {
			padding-left: 57%;
			text-indent: -132%;
		}
	
		.itemupdate .group>li:not(:first-child) {
			padding-left: 57%;
		}
	
		img.myimg,
		.doreamon .cover img,
		img.media-object.img-thumb {
			height: 22vh;
		}
	}
	
	@media screen and (max-width: 133vh) {
		.chapter-content .chapter-img {
			border-radius: 2.5mm;
			box-shadow: 0 0 2mm rgba(0, 0, 0, .5);
		}
	}
	
	@media screen and (min-width: 133vh) {
		.chapter-content .chapter-img {
			max-width: 50%;
		}
	
		.chapter-content .chapter-img:first-of-type,
		.chapter-content .chapter-img:last-of-type {
			border-radius: 2.5mm;
			box-shadow: 0 0 3mm rgba(0, 0, 0, .5);
		}
	}
}

@-moz-document domain("loveheaven.net")
{
	.chapter-content {
		font-size: 0;
	}
	
	.chapter-content .chapter-img {
		display: block;
	}
	
	@media screen and (max-width: 133vh) {
	
		#content,
		.chapter-content {
			max-width: calc((100vh - 25mm)/1.4);
			direction: rtl;
			font-size: 0;
		}
	}
	
	@media screen and (min-width: 133vh) {
		font[color="white"] .chapter-img {
			max-width: 50%;
			float: right;
		}
	
		.chapter-content .chapter-img:nth-last-of-type(1),
		.chapter-content .chapter-img:nth-last-of-type(2) {
			float: none;
		}
	
		.chapter-content .chapter-img:first-of-type,
		.chapter-content .chapter-img:last-of-type {
			clear: both
		}
	
		.chapter-content .chapter-img:nth-of-type(odd):not(:last-of-type) {
			border-radius: 0 2.5mm 2.5mm 0;
			box-shadow: 1.7mm 0 2mm rgba(0, 0, 0, .5);
		}
	
		.chapter-content .chapter-img:nth-of-type(even):not(:first-of-type) {
			border-radius: 2.5mm 0 0 2.5mm;
			box-shadow: -1.7mm 0 2mm rgba(0, 0, 0, .5);
		}
	}
}

@-moz-document domain("loveha.net"), domain("18lhplus.com"), domain("batoscan.net")
{
	.chapter-content {
		direction: rtl;
	}
	
	.chapter-content .chapter-img {
		display: inline-block;
	}
	
	#content>img:first-of-type,
	.chapter-img:first-of-type {
		display: block;
	}
	
	@media screen and (min-width: 133vh) {
		.chapter-content .chapter-img:nth-of-type(even):not(:last-of-type) {
			border-radius: 0 2.5mm 2.5mm 0;
			box-shadow: 1.7mm 0 2mm rgba(0, 0, 0, .5);
		}
	
		.chapter-content .chapter-img:nth-of-type(odd):not(:first-of-type) {
			border-radius: 2.5mm 0 0 2.5mm;
			box-shadow: -1.7mm 0 2mm rgba(0, 0, 0, .5);
		}
	}
}
