style.css:
@-moz-document url-prefix("https://duelingnexus.com/game/") {
  @font-face {
    font-family: "Source Sans Pro";
    src: url("https://rawcdn.githack.com/sefeiba/duelingnexus/c46f2a745dbf849dff5bccbebb792e5d63fcd60d/Source_Sans_Pro/SourceSansPro-Regular.ttf") format("truetype");
    font-weight: 400;
    text-rendering: optimizeLegibility;
  }
  
 @font-face {
    font-family: "Source Sans Pro";
    src: url("https://rawcdn.githack.com/sefeiba/duelingnexus/c46f2a745dbf849dff5bccbebb792e5d63fcd60d/Source_Sans_Pro/SourceSansPro-SemiBold.ttf") format("truetype");
    font-weight: 600;
    text-rendering: optimizeLegibility;
  }

@font-face {
    font-family: "Source Sans Pro";
    src: url("https://rawcdn.githack.com/sefeiba/duelingnexus/c46f2a745dbf849dff5bccbebb792e5d63fcd60d/Source_Sans_Pro/SourceSansPro-Bold.ttf") format("truetype");
    font-weight: 700;
    text-rendering: optimizeLegibility;
  }

@font-face {
    font-family: "Source Sans Pro";
    src: url("https://rawcdn.githack.com/sefeiba/duelingnexus/c46f2a745dbf849dff5bccbebb792e5d63fcd60d/Source_Sans_Pro/SourceSansPro-Black.ttf") format("truetype");
    font-weight: 900;
    text-rendering: optimizeLegibility;
  }

  body {
    margin: 0;
    overflow: hidden;
    color: #f0f0f0;
    font-family: "Source Sans Pro", sans-serif;
    font-size: 14px;
  }

  html {
    background: url("/*[[background]]*/") no-repeat center center fixed !important;
    background-size: cover !important;
    /*
    background-position: 0% 0% !important;
    background-size: 123.5% !important;
    */
  }

  /*
  #game-field-player-hand > div:nth-child(1) > img:nth-child(n) {
        height: 120px !important;
  }
  */
  /*testing*/
  
  #game-rps-first {
      all: unset;
      background-image: url(https://rawcdn.githack.com/sefeiba/duelingnexus/ddaabe4208012b92090a7ea66bc1fe1b79698c7a/rps/DN_-_Go_First.png);
      background-repeat: no-repeat;
      background-position: 50% 50%;
      /* put the height and width of your image here */
      height: 200px;
      width: 200px;
      border: none;
      color:rgba(0, 0, 0, 0);
      cursor: pointer;
  }

  #game-rps-second {
      all: unset;
      background-image: url(https://rawcdn.githack.com/sefeiba/duelingnexus/ddaabe4208012b92090a7ea66bc1fe1b79698c7a/rps/DN_-_Go_Second.png);
      background-repeat: no-repeat;
      background-position: 50% 50%;
      /* put the height and width of your image here */
      height: 200px;
      width: 200px;
      border: none;
      color:rgba(0, 0, 0, 0);
      cursor: pointer;
  }

  #game-rps-rock, #game-rps-paper, #game-rps-scissors {
      background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/ddaabe4208012b92090a7ea66bc1fe1b79698c7a/rps/DN_-_Rock.png) no-repeat;
      box-sizing: border-box;
      width: 200px;
      height: 200px;
      padding-bottom: 200px;
  }

  #game-rps-paper {
      background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/ddaabe4208012b92090a7ea66bc1fe1b79698c7a/rps/DN_-_Paper.png) no-repeat;
      box-sizing: border-box;
      width: 200px;
      height: 200px;
      padding-bottom: 200px;
  }

  #game-rps-scissors {
      background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/ddaabe4208012b92090a7ea66bc1fe1b79698c7a/rps/DN_-_Scissor.png) no-repeat;
      box-sizing: border-box;
      width: 200px;
      height: 200px;
      padding-bottom: 200px;
  }

  #game-rps-container {
    background-color: rgba(0, 0, 0, 0.7);
    height: 100vh;
    backdrop-filter: blur(7px);
    margin-top: 0px;
    padding-top: 5vh;
  }

  #game-rps-container > * {
    transform: translateY(10vh);
  }
  
  #game-action-menu {
    min-width: 8vw;
  }

  #game-room-players {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: auto auto auto auto auto repeat(4, 1fr);
    grid-column-gap: 0px;
    grid-row-gap: 0px;
  }

  #game-room-players > h4 {
    grid-area: 1 / 1 / 2 / 6;
  }

  #game-room-players-table {
    grid-area: 2 / 1 / 3 / 6;
  }

  #game-room-left-buttons {
    grid-area: 3 / 1 / 4 / 4;
  }

  #game-room-right-buttons {
    grid-area: 3 / 4 / 5 / 5;
    margin-bottom: 0px;
  }

  #game-room-deck-selection {
    grid-area: 4 / 1 / 5 / 4;
  }

  #game-deck-dropdown {
    margin-top: 6px;
    backdrop-filter: none !important;
  }

  #game-start-button {
    margin-top: 0px;
    backdrop-filter: none !important;
  }

  #game-room-left-buttons {
    margin-bottom: 0px;
  }

  #game-not-ready-button, #game-ready-button {
    margin-bottom: 9px !important;
    backdrop-filter: none !important;
  }

  #game-room-player1-kick-button, #game-room-player2-kick-button, #game-room-player3-kick-button, #game-room-player4-kick-button {
    border-radius: 10px;
    width: 40%;
    transform: translate(6px);
    backdrop-filter: none !important;
  }

  #game-room-players-table > tbody > tr:nth-child(n) > td:nth-child(3) {
    text-align: center;
  }

  #game-to-duelist-button, #game-to-spectator-button, #game-ready-button, #options-reset-button, #options-hide-button, #game-yesno-yes-button, #game-yesno-no-button, #game-end-button, #game-option-list > button:nth-child(n) {
    backdrop-filter: none !important;
  }
  
  .game-field-card, .engine-button, .game-field-zone > div > p, .game-card-negated
  {
    cursor: pointer;
  }
  #card-picture {
    cursor: grab;
  }
  #card-picture:active {
    cursor: grabbing;
  }
  
  #options-window > hr:nth-child(5) {
    display: none;
  }

  #options-window > p:nth-child(6) {
    display: grid;
    grid-template-columns: auto auto;
    margin-top: 12px;
  }

  #options-window > hr:nth-child(2) {
    color: rgba(187, 225, 250, 0.7);
    background-color: rgba(187, 225, 250, 0.7);
  }
  
  #game-position-def-up {
    margin-right: auto !important;
  }

  @keyframes game-card-hover {
    from {
      outline-color: #f4eeff;
      opacity: 1;
      /*
            filter: brightness(80%);
            -webkit-filter: brightness(80%);
            */
      box-shadow: 0 0 0.23vw 0.23vw #f4eeff;
    }
    50% {
      outline-color: #bbe1fa;
      opacity: 1;
      /*
            filter: brightness(120%);
            -webkit-filter: brightness(120%);
            */
      box-shadow: 0 0 0.23vw 0.23vw #bbe1fa;
    }
    to {
      outline-color: #e0fffb;
      opacity: 1;
      /*
            filter: brightness(120%);
            -webkit-filter: brightness(120%);
            */
      box-shadow: 0 0 0.23vw 0.23vw #e0fffb;
    }
  }
  
  .engine-window-frame {
    padding: 6px;
    padding-right: 1vw;
    padding-left: 1vw;
  }

  /*testing*/
  .game-selection-card-image,
  #game-position-atk-up,
  #game-position-def-up,
  #game-position-atk-down,
  #game-position-def-down {
    box-shadow: 0 0 0px 0px #bbe1fa;
  }

  .game-selection-card-image:hover,
  #game-position-atk-up:hover,
  #game-position-def-up:hover,
  #game-position-atk-down:hover,
  #game-position-def-down:hover {
    /*
    outline: 0 solid rgba(0, 0, 0, 0);
    */
    outline: 0.1vw solid rgba(0, 0, 0, 0);
    
    animation-name: game-card-hover;
    animation-duration: 1.5s;
    animation-iteration-count: infinite;
    animation-direction: alternate;
    animation-timing-function: linear;
    cursor: pointer;
    filter: none;
    /*
        filter: brightness(140%);
        -webkit-filter: brightness(140%);
        */
  }

  .game-selection-card {
    margin-bottom: 6px;
    margin-top: 6px;
    margin-left: 0px;
    width: fit-content;
  }

  #game-position-window > p {
    margin-top: 6px;
    margin-bottom: 9px;
  }

  #game-selection-list {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(8vw, 0fr));
    justify-items: center;
    justify-content: center;
  }

  .game-field-zone-content {
    height: 96% !important;
  }
  
  .game-field-zone-content p,
  #game-player-name,
  #game-opponent-name,
  .game-avatar-area,
  #card-if-monster,
  #card-if-spell {
    font-weight: 600;
  }

  #card-id {
    margin: 0px 0px 0px 0px;
  }

  #game-option-list {
    display: grid;
    /*
    grid-template-columns: repeat(2, 1fr);
    */
  }

  #game-option-list > button:nth-child(n) {
    border-radius: 10px;
    margin: 6px;
    margin-bottom: 3px;
  }
  
  *:focus {outline: none;}

  #ci-ext-misc-buttons {
    background-color: rgba(0, 0, 0, 0.8);
  }

  #game-selection-window {
    width: 74%;
    margin-left: 25.4%;
    margin-right: 0px;
    padding-right: 0;
    padding-left: 0;
    padding-top: 1.5vh;
    padding-bottom: 1vh;
    z-index: 201;
    max-height: 96%;
    overflow-x: hidden;
    overflow-y: auto;
  }

  #game-container {
    margin: 24px 16px 0px 16px;
    margin: 0px 0px 0px 0px;
  }

  #game-column {
    margin: 0px 0px 0px 0px;
    padding-top: 24px;
    height: 100vh;
    background-color: rgba(0, 0, 0, 0.5);
  }

  #game-chat-area {
    left: 0px;
    bottom: 0px;
    width: 25%;
  }

  #game-chat-textbox {
    box-shadow: 0 0 3px 0px black, inset 0 0 10px 1px black;
    border-radius: 10px;
    color: white;
    border: 1px solid rgba(187, 225, 250, 0.7);
    margin-left: 10px;
    margin-right: 10px;
    margin-bottom: 10px;
    padding-left: 10px;
    width: calc(25vw - 20px);
    background-color: rgba(10, 10, 10, 0.5);
    transition: 0.2s;
  }

  #game-chat-textbox:hover {
    box-shadow: 0 0 3px 0px black, inset 0 0 10px 1px black;
    border-radius: 10px;
    color: white;
    border: 1px solid rgba(187, 225, 250, 0.7);
    margin-left: 10px;
    margin-right: 10px;
    margin-bottom: 10px;
    padding-left: 10px;
    background-color: rgba(50, 50, 50, 0.7);
  }

  #game-chat-content {
    background-color: transparent;
    margin-left: 10px;
    margin-right: 10px;
  }

  .game-field-zone {
    position: relative;
    border: 0px;
    background-size: cover !important;
  }

  .game-field-zone:hover {
    border-radius: 0.4vw;
    box-shadow: inset 0 0 1.4vw #2e86de, 0 0 0.1vw #2e86de;
    background-color: transparent;
  }

  #card-description-box {
    background-color: rgba(0, 0, 0, 0);
    border: 0px solid;
    padding: 4px;
  }
  #card-name.engine-dark-box {
    background-color: rgba(0, 0, 0, 0);
  }
  #card-name {
    padding: 0px 0px 0px 0px;
    font-weight: 600;
  }
  #card-column {
    background-color: rgba(0, 0, 0, 0.8);
    min-height: 100vh;
    padding: 16px 16px 16px 16px;
    height: 100vh;
    max-height: 100vh;
    border-bottom-style: solid;
    border-bottom-width: 52px;
    border-color: transparent;
    backdrop-filter: blur(7px);
    border-right: 1px solid rgba(187, 225, 250, 0.7);
  }

  #card-picture {
    box-shadow: black 0px 0px 8px 0px;
    border-radius: 3px;
    max-width: /*[[crdpctrsz]]*/ ;
  }

  #game-highlight-card-img {
    width: /*[[crdpctrsz]]*/ ;
  }

  #game-field-extra-monster1.game-field-zone {
    background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/3e339f537272929619e4b066c7409b71ffa72dc2/linkzone.svg)
      no-repeat center center;
  }

  #game-field-extra-monster2.game-field-zone {
    background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/3e339f537272929619e4b066c7409b71ffa72dc2/linkzone.svg)
      no-repeat center center;
  }

  #game-field-opponent-monster5.game-field-zone,
  #game-field-opponent-monster4.game-field-zone,
  #game-field-opponent-monster3.game-field-zone,
  #game-field-opponent-monster2.game-field-zone,
  #game-field-opponent-monster1.game-field-zone {
    background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/3e339f537272929619e4b066c7409b71ffa72dc2/mobsterzone.svg)
      no-repeat center center;
  }

  #game-field-player-monster5.game-field-zone,
  #game-field-player-monster4.game-field-zone,
  #game-field-player-monster3.game-field-zone,
  #game-field-player-monster2.game-field-zone,
  #game-field-player-monster1.game-field-zone {
    background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/3e339f537272929619e4b066c7409b71ffa72dc2/mobsterzone.svg)
      no-repeat center center;
  }

  #game-field-player-spell6.game-field-zone {
    background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/2bb242fe7d4fb67fb3f9d0df457430275d6955d2/spellzonenew.svg)
      no-repeat center center;
  }

  #game-field-opponent-graveyard.game-field-zone {
    background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/63d06398a3c94823a524c66ee33b70ce50187f52/gy.svg)
      no-repeat center center;
  }

  #game-field-opponent-spell6.game-field-zone {
    background: url(https://rawcdn.githack.com/sefeiba/duelingnexus/2bb242fe7d4fb67fb3f9d0df457430275d6955d2/spellzonenew.svg)
