    #game-field-opponent-spell1.game-field-zone:hover,
    #game-field-opponent-spell2.game-field-zone:hover,
    #game-field-opponent-spell3.game-field-zone:hover,
    #game-field-opponent-spell4.game-field-zone:hover,
