  #game-field {
    background-color: rgba(0, 0, 0, 0);
  }

  /*
  @keyframes game-field-zone-flash {
    from {
      filter: brightness(80%);
      -webkit-filter: brightness(80%);
      box-shadow: inset 0 0 0 #2e86de, 0 0 0 #2e86de;
      border-radius: 0.4vw;
    }
    to {
      filter: brightness(120%);
      -webkit-filter: brightness(120%);
