  @keyframes game-field-zone-flash {
    from {
      /*
            filter: brightness(80%);
            -webkit-filter: brightness(80%);
            */
      box-shadow: inset 0 0 0.1vw #2e86de, 0 0 0.05vw #2e86de;
      border-radius: 0.4vw;
    }
    to {
      /*
            filter: brightness(120%);
            -webkit-filter: brightness(120%);
            */
      box-shadow: inset 0 0 0.8vw #2e86de, 0 0 0.4vw #2e86de;
      border-radius: 0.4vw;
    }
  }

  .game-field-zone.game-field-zone-selectable {
    cursor: pointer;
    animation: 0.5s game-field-zone-flash infinite alternate ease-out;
  }

  @keyframes game-selectable-border {
    from {
      
      outline-color: #f4eeff;
      
      opacity: 1;
      /*
            filter: brightness(80%);
            -webkit-filter: brightness(80%);
            */
      box-shadow: 0 0 0.13vw 0.13vw #f4eeff;
    }
    to {
      
      outline-color: #bbe1fa;
      
      opacity: 1;
      /*
            filter: brightness(120%);
            -webkit-filter: brightness(120%);
            */
      box-shadow: 0 0 0.23vw 0.23vw #bbe1fa;
    }
  }
  
    /*Firefox Fix*/
    /*Firefox Fix*/
    /*Firefox Fix*/
  @keyframes game-selectable-border-firefox {
    from {
      
      outline-color: #f4eeff;
      
      opacity: 1;
      /*
            filter: brightness(80%);
            -webkit-filter: brightness(80%);
            */
      box-shadow: 0 0 0.0vw 0.0vw #f4eeff;
