style.css:
@-moz-document domain('reddit.com') {
	:root {
		--bg: #222;
		--bg-light: #333;
		--txt: #888;
		--link: #ccc;
		--link-visited: #866;
	}

	body,
	#search input[type="text"],
	.side,
	.link .usertext-body .md,
	body.with-listing-chooser .listing-chooser .grippy,
	textarea,
	.infobar,
	.linkinfo,
	.shortlink input[id="shortlink-text"],
	.md,
	.flair,
	.linkflairlabel,
	body.with-listing-chooser .listing-chooser,
	.reddit-infobar .md,
	.titlebox form.flairtoggle,
	.sidebox .spacer,
	.sidebox .subtitle,
	.login-required.access-required,
	.reddit-infobar.md-container-small.with-icon.archived-infobar,
	.roundfield textarea,
	.roundfield input[type="text"],
	.roundfield input[type="url"],
	.roundfield input[type="password"],
	.roundfield input[type="number"],
	#compose-message .roundfield select,
	.md > :last-child,
	.md .-cells > :last-child,
	.md .-lists > :last-child,
	.md .-blocks > :last-child,
	.md .-headers > :last-child,
	.md h1 > :last-child,
	.md h2 > :last-child,
	.md h3 > :last-child,
	.md h4 > :last-child,
	.md h5 > :last-child,
	.md h6 > :last-child,
	.md th > :last-child,
	.md td > :last-child,
	.md ul > :last-child,
	.md ol > :last-child,
	.md .-lists > :last-child,
	.md pre > :last-child,
	.md blockquote > :last-child,
	.md table > :last-child,
	.md p > :not(a):last-child,
	.md ul > :last-child,
	.md ol > :last-child,
	.roundfield,
	.author-tooltip,
	.author-tooltip__head,
	.message.new > .entry,
	.usertext.border .usertext-body,
	.crosspost-preview,
	.new-comment .md :not(pre) > code,
	.link .md :not(pre) > code,
	.usertext.border .md :not(pre) > code,
	.new-comment .md pre,
	.link .md pre,
	.usertext.border .md pre,
	#header {
		background-color: var(--bg);
		color: var(--txt);
	}
	#header-bottom-right,
	.tabmenu li a {
		background-color: var(--bg-light);
	}
	.thing.link.promoted.promotedlink,
	.nub {
		display: none;
	}
	.thumbnail.default:before,
	.thumbnail.self:before {
		content: "";
		position: absolute;
		top: 0;
		right: 0;
		bottom: 0;
		left: 0;
		border-radius: 100%;
		width: 73%;
		height: 105%;
		margin: auto;
		background-color: black;
		opacity: .5;
	}
	.default-header#header-img {
	    background-image: url(https://bit.ly/2VgdFpT);
	    background-position: center;
	    background-size: 95%;
	}
	.thing .title {
		color: var(--link);
	}
	.thing .title:visited {
		color: var(--link-visited);
	}
}
