style.css:
@-moz-document url-prefix("https://cointelegraph.com/") {
/* General settings & backgrounds */
div.post-page, div.header-zone.layout__header, .tag-page{ background-color: #202020 !important;}
div.layout__header{ margin-bottom: 0 !important;}


/* Article colors */
article.post__article, .post-meta__author-name, .post-meta__publish-date, .post-content>:not(div), .post-content>:not(div) a,
.editors-choice-widget__title, .editors-choice-widget__link, .related-list__title, .related-list__item-title, .price-widget-item,
.post-card-inline__title-link, .post-card-inline__text
{ color: #fff !important;}


/* Ads and unwanted content blocker */
div.privacy-policy.bottom-zone__privacy-policy, .sidebar__widget .banner-youtube, .post__content-shares,
.sidebar__widget:not(:first-child):not(:last-child)
{ display: none !important;}
}
