/* PAGINATION - HIGHLIGHT - === */
.NumberedPager a.Highlight ,
#PagerBefore.Pager a.Highlight{
    background: yellow ;
    color: red ;
}

/* (new175) ALL - PAGINATOR CENTER -  HIGHTLIGHT / PREV / NEXT  - === */
.Pager a, 
.Pager span {
    position: relative ;
    display: inline-block ;
    vertical-align: middle ;
    min-width: 17px ;
    height: 14px ;
    line-height: 13px ;
    margin: 0 3px;
    padding: 0 ;
    text-align: center ;
    border-radius: 3px ;
box-shadow: 3px 3px 2px black ;
background: #222 ;
}
.NumberedPager a.Next, 
.NumberedPager span.Next, 
.NumberedPager a.Previous, 
.NumberedPager span.Previous {
    position: relative ;
    display: inline-block ;
    height: 14px ;
    line-height: 11px ;
    font-size: 23px ;
    border-radius: 3px ;
    vertical-align: middle ;
box-shadow: 3px 3px 2px black ;
background: #222 ;
}
.NumberedPager .Next:hover, 
.NumberedPager .Previous:hover {
    position: relative ;
    display: inline-block ;
    vertical-align: middle ;
    height: 14px ;
    line-height: 11px ;
    border-radius: 3px ;
    top: 5px ;
    left: 0px ;
    margin-left: 3px ;
    right: 0px ;
    font-size: 26px ;
}
#PagerAfter>a:hover,
#PagerBefore>a:hover,
body .Pager .Highlight:hover,
.PageControls .Pager a:hover  {
    display: inline-block ;
color: tomato ;
}
#PagerBefore> .Next:hover,
.NumberedPager a.Next:hover,
.NumberedPager span.Next:hover {
    position: relative ;
    display: inline-block ;
    height: 20px ;
    line-height: 17px ;
    margin-top: -10px ;
    font-size: 23px ;
    vertical-align: middle ;
color: tomato ;
}
#PagerAfter> .Previous:hover,
#PagerBefore> .Previous:hover,
.NumberedPager a.Previous:hover,
.NumberedPager span.Previous:hover {
    position: relative ;
    display: inline-block ;
    height: 20px ;
    line-height: 17px ;
    margin-top: -10px ;
    font-size: 23px ;
    vertical-align: middle ;
color: tomato ;
}
.p-100.LastPage:hover {
    position: relative ;
    display: inline-block ;
    height: 20px ;
    line-height: 17px ;
    font-size: 16px ;
    vertical-align: middle ;
color: tomato ;
}
