  0%   {opacity: 0;}
  40%  {opacity: 0;}
  100% {opacity: 1;}
}
@keyframes fade3 {
  0%   {opacity: 1;}
  100% {opacity: 0;}
}
@keyframes fade4 {
  0%   {opacity: 0;}
