css:
@-moz-document url-prefix("https://www.google.com/search") {
.mw,
.lEXIrb {
  max-width: max-content !important;
  width: 100% !important;
  padding: 0 40px;
  box-sizing: border-box;
}
.mw #rhs {
  display: none;
}
#top_nav {
  display: flex;
  justify-content: center;
}
#rhs .rhsvw {
  width: 457px;
}
.kp-blk {
  border: 0;
  box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.3);
}
.kno-ftr {
  margin-top: 5px;
}
#rcnt > .col:nth-child(2),
#rcnt > .D6j0vc {
  width: 100% !important;
  box-sizing: border-box;
}
#cnt #center_col,
.mw #center_col {
  margin-left: 0;
}
#cnt #center_col,
#cnt #foot {
  width: 100%;
}
#rso {
  counter-reset: results;
}
.vk_c {
  width: auto !important;
}
.g,
.ts {
  display: inline-block;
  width: 100% !important;
  padding: 15px 20px;
  border-radius: 5px;
  box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.3);
  margin-bottom: 20px;
  position: relative;
  box-sizing: border-box;
  transform: translateZ(0);
  z-index: 1;
}
.ts {
  padding: 20px;
}
.vk_c,
.vk_cxp {
  margin-left: 0;
  margin-right: 0;
}
div.AEprdc.vk_c {
  margin-bottom: 15px;
  border: none;
  box-shadow: 1px 1px 5px rgba(0, 0, 0, 0.3);
}
.COEoid {
  margin-left: 0;
  margin-right: 0;
}
/* .g.g-blk {
  padding: 0 0 15px;
} */
.g.g-blk .g {
  box-shadow: none;
  padding: 0 15px;
}
#rso > .g:before,
.srg > .g:before {
  counter-increment: results;
  content: counter(results);
  z-index: -10;
  color: #eee;
  font-size: 4.5em;
  position: absolute;
  text-align: right;
  line-height: 1em;
  top: 6px;
  right: 6px;
}
.g .s {
  max-width: max-content;
}
.g table {
  width: 100%;
  margin: 0;
  table-layout: fixed;
}
.g table .s .st {
  width: auto !important;
}
.g table .mslg .sld {
  width: auto;
}
.QV1uNc {
  margin: 0;
}
.kno-ftr {
  margin-right: 0;
}
.mslg .vsc {
  width: 100%;
}
#center_col .kp-blk {
  width: auto;
  box-shadow: none;
}
#taw {
  display: none;
}
.P1usbc {
  white-space: normal;
}
.brs_col {
  display: flex;
  justify-content: center;
}
g-section-with-header {
  margin: 15px 0 20px;
  display: flex;
  flex-direction: column;
}
g-section-with-header > div {
  max-width: 100%;
}
g-section-with-header h3 {
  text-align: center !important;
}
#extrares {
  display: flex;
  justify-content: center;
}
@media (min-width: 1201px) {
  /* 搜索框顶部 */
  #tsf {
    margin: auto;
    display: flex;
    justify-content: center;
    transform: translateX(70px);
  }
  #hdtbSum {
    display: flex;
    justify-content: center;
  }
  #hdtbMenus {
    display: flex;
    justify-content: center;
  }
  #XJDHvf {
    width: 0;
  }
  body.vasq .WE0UJf {
    display: flex;
    justify-content: center;
    margin-left: 0;
  }
  #result-stats {
    position: static;
  }
}
.srg > div,
#rso > div {
  break-inside: avoid;
}
.srg,
#rso {
  column-gap: 20px;
}
@media (min-width: 1201px) and (max-width: 1700px) {
  .srg,
  #rso {
    column-count: 2;
    -moz-column-count: 2;
    -webkit-column-count: 2;
  }
}
@media (min-width: 1701px) and (max-width: 2200px) {
  .srg,
  #rso {
    column-count: 3;
    -moz-column-count: 3;
    -webkit-column-count: 3;
  }
}
@media (min-width: 2201px) {
  .srg,
  #rso {
    column-count: 4;
    -moz-column-count: 4;
    -webkit-column-count: 4;
  }
}
}
