css:
@-moz-document url-prefix("https://forums.mihoyo.com/genshin/")
{
html, body {
    background-color: #1F1F1F;
}

.root-page-container {
    background: url(https://i.imgur.com/g3P0IUm.png) no-repeat 0 62px;
}

.mhy-container, .mhy-home__post-wrp, .mhy-side-section, .mhy-layout__subtitle, .mhy-article-list__empty {
    background-color: #313131;
    color: #DCDCDC;
}

.mhy-tab__item a, .mhy-tab__item span, .mhy-topic-card__link, .mhy-op-card__link, .mhy-account-title__name, .mhy-social-link-list__card span, .mhy-img-article-card__account a, .header-notification__text, .header-notification__name, .header__navmore, .mhy-notification-card__content, .mhy-input__container input, .mhy-input__container textarea, .mhy-forum-card__name, .mhy-reply-box__textarea .reply-editor, .reply-card .reply-card-inner-reply__content, .mhy-img-text-article__content.ql-editor p, .ql-snow *, .mhy-message-box__container, .form-item-container, .form-img-auth-type .mhy-select .mhy-select__label, input {
    color: #DCDCDC !important;
}

.mhy-article-card__h3, .mhy-article-list-top__title a, .mhy-news-card__title, .mhy-news-card__commentnum, .mhy-img-article-card__header a, .mhy-account-center-header__data-num {
    color: #ed781e;
}

.mhy-article-card:hover, .mhy-img-article-card, .header__navitem--show, .mhy-selectmenu, .mhy-notification-card:hover, .mhy-button-outlined .mhy-button__button:hover, .s-reply-list__title--showall, .reply-detail-container .mhy-reply-list, .s-reply-list__title, .mhy-account-center-post-card:hover, .mhy-follow-button.mhy-follow-button-lg:not(.mhy-follow-button--follow):not(.mhy-follow-button--follow-followed):hover, .mhy-article-page-info, .mhy-reply-box__textarea, .editor-container #toolbar.ql-toolbar, .mhy-message-box__container, .mhy-message-box__content, .mhy-topic-label-selector__add:hover, .form-item-container__img {
    background-color: #3e3e3e;
}

.mhy-home__post-wrp, .mhy-side-section, .mhy-home-list__header-box--fixed, .mhy-notification-card__origin, .mhy-button-outlined .mhy-button__button, .mhy-account-center-reply-card__origin, .mhy-article-page-author, .mhy-follow-button.mhy-follow-button-lg, .mhy-article-actions, .mhy-article-page-reply-action, .reply-card__replies, .reply-card .reply-card-inner-reply-operation, .edit-link-sheet .mhy-input .mhy-input__container, .form-item-container__describe .mhy-input__maxtip, input {
    background-color: #313131;
}

.mhy-home-list__header-box, .mhy-side-section__header, .mhy-article-user-setting, .mhy-article-list-top, .mhy-layout__subtitle, .header__notification--show .mhy-tab, .header-notification__item, .mhy-side-menu__header, .mhy-notification__subheader, .mhy-account-center__menu li:nth-of-type(3), .mhy-account-center__subheader, .mhy-action-sheet__header {
    border-bottom: 1px solid #3e3e3e;
}

.mhy-article-card+.mhy-article-card, .mhy-news-card+.mhy-news-card, .mhy-container__footer, .header__notifyfooter, .mhy-notification-card+.mhy-notification-card .mhy-notification-card__inner, .footer__bottom, .mhy-article-page-user-data, .mhy-article-actions__item+.mhy-article-actions__item, .mhy-account-center-post-card, .mhy-account-center-fan-card+.mhy-account-center-fan-card {
    border-top: 1px solid #3e3e3e;
}

.header .header__logout, .reply-card+.reply-card, .mhy-article-page-reply-list__loadmore {
    border-top: 1px solid #313131;
}

.mhy-message-box__header {
    border-bottom: 1px solid #313131;
}

.mhy-rocket__icon-r {
    background-image: url(https://i.imgur.com/Rz0h6T8.png);
    background-color: #313131;
}

.mhy-rocket__icon-r:hover {
    background-image: url(https://i.imgur.com/Z4SQFpn.png);
}

.mhy-op-card__title:hover{
    color: #dcbc60;
}

.mhy-tab__item, .mhy-topic-label-selector__item, .header__notification--show {
    background-color: #313131;
    color: #DCDCDC;
}

.mhy-home-list .mhy-article-filter .mhy-tab__item, .mhy-selectmenu {
    border: 1px solid #3e3e3e;
}

.footer {
    background: #313131;
}

.footer__itemtext a:hover {
    color: #dcbc60;
}

.mhy-forum-header {
    background-color: #313131;
    border-bottom: 1px solid #3e3e3e
}

.mhy-news-card__commentnum {
    font-weight: bold;
}

.mhy-tab__item--active span, .mhy-tab__item--active a {
    color: #dcbc60;
}

.header-notification__subtitle, .mhy-notification-card__subtext {
    color: #666;
}

.header__navmore:hover {
    background-color: #2d2f33;
}

.mhy-table thead {
    background-color: #313131;
    color: #C9C9C9;
}

.mhy-table tbody tr:nth-child(even) {
    background-color: #313131;
}

.mhy-level-status__right, .mhy-account-center-header__data-item+.mhy-account-center-header__data-item {
    border-left: 1px solid #3e3e3e;
}

.mhy-table td {
    border: 1px solid #3e3e3e;
}

.mhy-action-sheet__content, .reply-card, .reply-card__content {
    background-color: #3e3e3e;
    color: #C9C9C9;
}

.s-reply-list__title--showall, .reply-detail-container__reply {
    border-bottom: 1px solid #313131;
}

::-webkit-scrollbar-track-piece
  {
    background-color: #212121;
    -webkit-box-shadow: inset 0 0 0 rgba(0, 0, 0, 0) !important;
    border-radius: 0 !important;
    border: none !important;
  }

::-webkit-scrollbar {
    background-color: rgba(62, 62, 62, .92);
    border: 1.5px solid rgb(62, 62, 62);
    width: 15px;
}

::-webkit-scrollbar-thumb {
    background-color: rgba(31, 31, 31, 1);
    border: 1.5px solid rgb(62, 62, 62);
}
}
