/*spinner*/
.ytp-spinner-circle {
  width: 0px;
  height: 0px;
  top: 50%;
  left:50%;
  position: absolute!important;
  border: 4px solid #ff9d00!important;
  opacity: 1;
  border-radius: 50%!important;
  animation: lds-ripple 1s cubic-bezier(0, 0.2, 0.8, 1) infinite!important;
  animation-delay: 0s!important;
  box-shadow: 0px 0px 9px 5px #110108
}

@keyframes lds-ripple {
  0% {
	opacity: 1;
    top: 36px;
    left: 36px;
    width: 0;
    height: 0;
	background-color: #f00;
	background-image: url(https://i.ibb.co/7zyYyyW/yadburst.gif);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 200px;
  }
  100% {
	opacity: 0;
    top: 0px;
    left: 0px;
    width: 72px;
    height: 72px;
	box-shadow: 0px 0px 9px 9px #fd0
  }
}
.ytp-spinner-left {
    position: initial;
    top: 0;
    left: 0;
    bottom: 0;   
}
.ytp-spinner-right {
    position: initial;
    top: 0;
    right: 0;
    bottom: 0;
}
.ytp-spinner-rotator {
    position: absolute;
    width: 100%;
    height: 100%;
    -webkit-animation: unset;
    animation: unset;
}
.ytp-spinner-container {
    
    animation: ytp-spinner-linspin 0ms linear infinite;
    -webkit-animation: ytp-spinner-linspin 0ms linear infinite;
}




