/* scrollbar main */
::-webkit-scrollbar {
  width: 6px;
}
/* Track */
::-webkit-scrollbar-track {
  box-shadow: inset 0 0 5px #00000021; 
  border-radius: 0px;
  background: #0000006b;
  background-image: linear-gradient(-182deg, #ff000045, #ff8f0045, #c800ff30);
} 
/* Handle */
::-webkit-scrollbar-thumb {
  background: #ff6f002b; 
  border-radius: 10px;
  background-image: linear-gradient(-182deg, #eb14140f, #eb8d1417, #eaeb1414);
  background-repeat: no-repeat;
  background-position: center;
}
/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #b3000045;
  background-image: linear-gradient(-182deg, #eb1414, #f15b04, #ebab14);
  background-repeat: no-repeat;
  background-position: center;
}

