yt-img-shadow.ytd-channel-list-sub-menu-avatar-renderer {
    --yt-img-border-radius: 40px;
    filter: hue-rotate(36deg) saturate(4.5) sepia(1) contrast(2.5) drop-shadow(0px 0px 3px #000) brightness(3.5);
}

/*premiering now*/
.badge-style-type-live-now.ytd-badge-supported-renderer {
    background: transparent;
    color: #fff;
    border: 1px dashed #8b8b8b00;
	animation: liveyad 1s infinite;
	animation-fill-mode: forwards;
	animation-timing-function: ease-in
}
@keyframes liveyad {
	25% {border-top: 1px solid #84ff00;}
	50% {border-left: 1px dashed #ffe600;}
	75% {border-bottom: 1px solid #ff6200;}
	90% {border-right: 1px dashed #f00;}
}

/*user channel*/
#channel-header.ytd-c4-tabbed-header-renderer {
    background-color: #cf060600;
}
#tabs-inner-container.ytd-c4-tabbed-header-renderer {
    background-color: #e15a0000;
}
ytd-browse[page-subtype=channels] {
    background: #0f0f0f00;
}
#channel-container.ytd-c4-tabbed-header-renderer, #tabs-container.ytd-c4-tabbed-header-renderer {
    background-color: #18181896;
	background-image: linear-gradient(-100deg, #1a1a1ac9 0%, #ff00268c 100%)
}

/*notification*/
#notification-count.ytd-notification-topbar-button-renderer {
    background-color: #e1951c;
	background-image: linear-gradient(230deg, #1a1a1ad6 0%, #ff0026d4 100%)
}

/*advisory*/
ytd-clarification-renderer[background-style=info] {
    background: #2614148a;
}

/*learn more*/
yt-button-renderer.style-suggestive[is-paper-button] paper-button.yt-button-renderer {
    border: 1px solid #ff7e00;
}
yt-button-renderer #button.yt-button-renderer {
    color: aliceblue;
}

ytd-rich-metadata-renderer {
    background-color: #20234961;
}

ytd-shared-post-renderer[uses-compact-lockup_] #repost-context.ytd-shared-post-renderer {
    background-color: #282828ad;
}

/*share dialog*/
paper-dialog {
	background-image: linear-gradient(190deg, #1a1a1ad6 0%, #ff002669 100%);
	background-color: #1d1212e0;
	color: #fff
}
#bar.yt-copy-link-renderer {
    border: 1px solid #f2000033;
    background-color: #070404ab;
}

/*survey*/
#star-survey.ytd-inline-survey-renderer {
    background-color: #2e2533b0;
}

#inline-survey-compact-video-renderer.ytd-inline-survey-renderer {
    background-color: #1e131396;
}

#title.ytd-inline-survey-renderer, #follow-up-title.ytd-inline-survey-renderer {
    color: #ff8e3ed1;
}
#subtitle.ytd-inline-survey-renderer {
    color: #ffffff;
}

/*top bar2*/
#chips-wrapper.ytd-feed-filter-chip-bar-renderer {
    background-color: #b01c1cf0;
    border-top: 1px solid #f00;
    border-bottom: 1px solid #f00;
}

